mapper.insert:

INSERT board
SET title = #{title}
, contents = #{contents}
, writer_id = #{writerId}

mapper.select:

SELECT board_id AS boardId, title, contents, writer_id AS writerId, hits, created_at AS createdAt, updated_at AS updatedAt
FROM board
WHERE board_id = #{boardId}

mapper.update:

UPDATE board
SET hits = hits + 1
WHERE board_id = #{boardId}


UPDATE board


        , title = #{title}


        , contents = #{contents}


WHERE board_id = #{boardId}
    AND writer_id = #{writerId}

mapper.delete:

DELETE FROM board
WHERE board_id = #{boardId}
    AND writer_id = #{writerId}
